import React, { useState, useEffect } from 'react';

const Friends = () => {
    useEffect(() => {
        fetch('http://127.0.0.1:8000/friends/list', {
            method: 'GET',
            credentials: 'include',
          })
          .then((resp) => {
            return resp.json();
          })
          .then((data) => {
            return friendsListHandler(data);
          })
          .catch((err) => {
            return console.error(err);
          })
        fetch('http://127.0.0.1:8000/friends/pending', {
            method: 'GET',
            credentials: 'include',
          })
          .then((resp) => {
            return resp.json();
          })
          .then((data) => {
            //return console.log(data);
            return friendsPendingHandler(data);
          })
          .catch((err) => {
            return console.error(err);
          })
    }, []);

    const [friendsList, setFriendsList] = useState([]);
    const [friendsPending, setFriendsPending] = useState(false);
    const [friendsPendingList, setFriendsPendingList] = useState([]);
    const [friendName, setFriendName] = useState('');

    const friendsListHandler = (props) => {
        console.log(props.friends);
        if (props.friends.length === 0) {
            setFriendsList('No friends yet.');
        } else { 
            // map this
            setFriendsList(props.friends);
        }
    };

    const friendsPendingHandler = (props) => {
        if (props.pending.length > 0) {
            setFriendsPending(true);
            setFriendsPendingList(props.pending);
        }
    }

    const handleAddFriend = (event) => {
        event.preventDefault();
        fetch('http://127.0.0.1:8000/friends/add/'+friendName, {
            method: 'GET',
            credentials: 'include',
          })
          .then((resp) => {
            return resp.json();
          })
          .then((data) => {
            return console.log(data);
          })
          .catch((err) => {
            return console.error(err);
          })
    };
    return (
        <div>
            <h1>Friends</h1>
            <div> 
            <form onSubmit={handleAddFriend}>
                <label>
                    Add a friend:
                    <input 
                        type="text"
                        value={friendName}
                        onChange={e => setFriendName(e.target.value)} 
                    />
                </label>
                    <input type="submit" value="+" />
                </form>
            </div>
            <div>
                {friendsList} 
            </div>
            {friendsPending && 
            <div>
                Friends pending: <br/>
                {friendsPendingList}
            </div>}
        </div>
    );
}

export default Friends;
