import styled from "styled-components";

export const Wrapper = styled.div`
  background: linear-gradient(to bottom, #e7ffb3, #707070);
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Header = styled.div`
  background: #333;
  height: 10vh;
`;
