import React, { useState , useEffect }from "react";

const Dashboard = () => {
  const [currentlyListening, setCurrentlyListening] = useState([]);
  const [recentlyPlayedList, setRecentlyPlayedList] = useState([]);


  // const currentTrack = {
  //   artist: currentlyListening.item.artists.name,
  //   name: currentlyListening.item.name, 
  //   album: currentlyListening.item.album.images[2].url,
  //   time: currentlyListening.item.timestamp
  // };

  const currentTrack = currentlyListening.items;
  const recentlyPlayedListHandler = (props) => {
    const array = [];
    for (let i = 0; i < props.length; i++) {
      array.push(
        <Item key={i} item={props.items[i].track.artists[i].name} />
      );
    }
    setRecentlyPlayedList(array);
  }

  useEffect(() => { 
      fetch('http://127.0.0.1:8000/spotify/user/recent_tracks', {
        method: 'GET',
        credentials: 'include',
      })
      .then((resp) => {
        return resp.json();
      })
      .then((data) => {
        return setCurrentlyListening(data);
      })
      // .catch((err) => {
      //   return console.error(err);
      // })
  })
  return (
    <div>
      {/* <h1>Dashboard</h1>
      <h1>
        Currently listening:
      </h1>
      <ul>
      <li>
        <img src={currentTrack.album} alt="exo album"/>
        {currentTrack.artist} - {currentTrack.name}
      </li>
      </ul>  */}
      <button onClick={()=> console.log(currentTrack[0].track.artists[0].name)}>
        play
      </button>
      <div>
        {recentlyPlayedList}
      </div>
    </div>
  )
};

export default Dashboard;
