import React, { useState, useEffect } from 'react';

const Ratings = () => {
    useEffect(() => {
        fetch('http://127.0.0.1:8000/ratings/list', {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            return ratingsListHandler(data);
        })
        .catch((err) => {
            return console.error(err);
        })
    }, []);

    let fetchedContentInfo = {
        type: '',
        uri: '',
        name: ''     
    };
    const [ratingsList, setRatingsList] = useState([]);

    const fetchRatingsInfoHandler = (content_id) => {
        fetch('http://127.0.0.1:8000/content/'+content_id, {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            console.log('Info Handler');
            console.log(data);
            fetchedContentInfo.type = data.type;
            fetchedContentInfo.uri = data.uri;
            fetchedContentInfo.name = data.name;
            return data;
        })
        .catch((err) => {
            return console.error(err);
        })
    }

    const ratingsListHandler = (props) => {
        //console.log('Ratings Handler');
        //console.log(props);
        const array = [];
        //console.log(props.ratings.length);
        for (let i = (props.ratings.length - 1); i >= 0; i--) {
            fetchRatingsInfoHandler(props.ratings[i].content_id);
            console.log(fetchedContentInfo);
            array.push({
                id: i,
                // name: fetchedContentInfo.name
            });
        }
        setRatingsList(array.map((array) => {
            return (
                <li key={array.id}>
                    {array.name}
                </li>
            );
        }))
    };

    return (
        <div>
            <h1>Ratings</h1>
            <div>
                <h2>Track ratings:</h2>
                {ratingsList}
            </div>
        </div>
    );
};

export default Ratings;
