import React, { useState, useEffect } from 'react';

const Ratings = () => {
    const [ratingsList, setRatingsList] = useState([]);
    useEffect(() => {
        fetch('http://127.0.0.1:8000/ratings/list', {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            return ratingsListHandler(data);
        })
        .catch((err) => {
            return console.error(err);
        })
    }, []);

    const [contentInfo, setContentInfo] = useState([]);
    const [contentFound, setContentFound] = useState(false);
    const fetchTrackInfo = (props) => {
        fetch('http://127.0.0.1:8000/spotify/user/fetch_tracks?tracks='+props, {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            console.log(data);
            setContentInfo(data);
            return setContentFound(true);
        })
        .catch((err) => {
            return console.error(err);
        })
    }

    const getContentInfoHandler = (props) => {
        fetch('http://127.0.0.1:8000/content/'+props, {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            console.log(data.uri);
            return fetchTrackInfo(data.uri);
        })
        .catch((err) => {
            return console.error(err);
        })
    }

    const ratingsListHandler = (props) => {
        console.log(props.ratings[0]);
        if (props.ratings.length === 0) {
            // User has no ratings;
        } else {
            const array = [];
            const k = props.ratings.length - 1;
            for (let i = k; i >= 0; i--) {
                array.push({
                    content_id: props.ratings[i].content_id,
                    rating: props.ratings[i].rating,
                    id: (k - i)
                });
            }
            console.log(array);   
            setRatingsList(array.map((array) => {
                return (
                    <li key={array.id} onClick={getContentInfoHandler.bind(getContentInfoHandler, array.content_id)}>
                        Rating: {array.rating}
                        { contentFound && <div>
                        <li>
                            <img src={contentInfo.tracks[0].album.images[1].url} alt={contentInfo.tracks[0].artists[0].name} />
                        </li>
                        <li>
                            Track name: {contentInfo.tracks[0].artists[0].name}
                        </li>
                        <li>
                            Artist: {contentInfo.tracks[0].artists[0].name}
                        </li>
                        <li>
                            Album: {contentInfo.tracks[0].album.name}
                        </li>
                        </div>}
                    </li>
                );
            }))
        }
        console.log(ratingsList);
    };

    return (
        <div>
            <h1>Ratings</h1>
            <div>
                <h2>Track ratings:</h2>
                {ratingsList}
            </div>
        </div>
    );
};

export default Ratings;
