import React, { useState , useEffect }from "react";

const Dashboard = () => {
  const [currentlyListening, setCurrentlyListening] = useState([]);

  const currentTrack = {
    artist: currentlyListening.item.artists.name,
    name: currentlyListening.item.name, 
    album: currentlyListening.item.album.images[2].url,
    time: currentlyListening.item.timestamp
  };

  useEffect(() => { 
      fetch('http://127.0.0.1:8000/spotify/user/playing_track', {
        method: 'GET',
        credentials: 'include',
      })
      .then((resp) => {
        return resp.json();
      })
      .then((data) => {
        return setCurrentlyListening(data);
      })
      // .catch((err) => {
      //   return console.error(err);
      // })
  })
  return (
    <div>
      <h1>Dashboard</h1>
      <h1>
        Currently listening:
      </h1>
      <ul>
      <li>
        <img src={currentTrack.album} alt="exo album"/>
        {currentTrack.artist} - {currentTrack.name}
      </li>
      </ul> 
      {/* <button onClick={()=> console.log(currentTrack)}>
        play
      </button> */}
    </div>
  )
};

export default Dashboard;
