import React, { useState, useEffect } from 'react';

const Ratings = () => {
    const [ratingsList, setRatingsList] = useState([]);
    useEffect(() => {
        fetch('http://127.0.0.1:8000/ratings/list', {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            const k = data.ratings.length - 1;
            console.log(k);
            console.log(data);
            for (let i = k; i >= 0; i--) {
                fetchImagesHandler(data.ratings[i]);
            }
            // return ratingsListHandler(data);
        })
        .catch((err) => {
            return console.error(err);
        })
        // eslint-disable-next-line
    }, []);
    const images = [];
    const fetchImagesHandler = (props) => {
        console.log(props);
        fetch('http://127.0.0.1:8000/spotify/user/fetch_tracks?tracks='+props.uri, {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            console.log(data);
            images.push(data.tracks[0].images[2].url);
        })
        .catch((err) => {
            return console.error(err);
        })
    };

    const ratingsListHandler = (props) => {
        console.log(props.ratings[0]);
        if (props.ratings.length === 0) {
            // User has no ratings;
        } else {
            const array = [];
            const k = props.ratings.length - 1;
            for (let i = k; i >= 0; i--) {
                array.push({
                    name: props.ratings[i].name,
                    uri: props.ratings[i].uri,
                    content_id: props.ratings[i].content_id,
                    rating: props.ratings[i].rating,
                    id: (k - i)
                });
            }
            console.log(array);   
            setRatingsList(array.map((array) => {
                const url = '/edit/track/'+array.uri;
                return (
                    <li key={array.id}>
                        {/* click to view / edit */}
                        <a href={url}>{array.rating}/10 - {array.name}</a>
                    </li>
                );
            }))
        }
        console.log(ratingsList);
    };

    return (
        <div>
            <h1>Ratings</h1>
            <a href='/dashboard'>Dashboard</a>
            <div>
                <h2>Track ratings:</h2>
                {ratingsList}
            </div>
        </div>
    );
};

export default Ratings;
