import React, { useState , useEffect }from "react";

const Dashboard = () => {
  useEffect(() => { 
      // fetch('http://127.0.0.1:8000/spotify/user/playing_track', {
      //   method: 'GET',
      //   credentials: 'include',
      // })
      // .then((resp) => {
      //   return resp.json();
      // })
      // .then((data) => {
      //   return setCurrentlyListening(data);
      // })
      // .catch((err) => {
      //   return console.error(err);
      // })

      fetch('http://127.0.0.1:8000/spotify/user/recent_tracks', {
        method: 'GET',
        credentials: 'include',
      })
      .then((resp) => {
        return resp.json();
      })
      .then((data) => {
        return recentlyPlayedListHandler(data.items);
      })
      // .catch((err) => {
      //   return console.error(err);
      // })
      checkIfUserIsEnabled();
  }, [])
  const [currentlyListening, setCurrentlyListening] = useState([]);
  const [recentlyPlayedList, setRecentlyPlayedList] = useState([]);

  // const currentTrack = currentlyListening.items;
  const recentlyPlayedListHandler = (props) => {
    const array = [];
    for (let i = 0; i < props.length; i++) {
      console.log(props[i].track.artists[0].name);
      array.push({
        id: i,
        value: props[i].track.artists[0].name + ' - ' + props[i].track.name
      });
    }
    setRecentlyPlayedList(array.map((array) =>
    <li key={array.id}>{array.value}</li>
  ));
    console.log(array);
    console.log(props);
  }
  return (
    <div>
      <h1>Dashboard</h1>
      {/* <button onClick={()=> recentlyPlayedListHandler(currentTrack)}>
        play
      </button> */}
      <div>
        <h2>Recentyly Played</h2>
        <ul>
        {recentlyPlayedList}
        </ul>
      </div>
    </div>
  )
};

export default Dashboard;
