import React, { useState, useEffect } from 'react';

const Ratings = () => {
    useEffect(() => {
        fetch('http://127.0.0.1:8000/ratings/list', {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            return ratingsListHandler(data);
        })
        .catch((err) => {
            return console.error(err);
        })

    }, []);

    // const [fetchedContentInfo, setFetchedContentInfo] = useState([]);
    let fetchedContentInfo = [];
    const [ratingsList, setRatingsList] = useState([]);

    const ratingsListHandler = (props) => {
        const array = [];
        if (props.ratings.length === 0) {
            // User has no ratings
        } else {
            const k = props.ratings.length - 1;
            for (let i = k; i >= 0; i--) {
                fetch('http://127.0.0.1:8000/content/'+props.ratings[i].content_id, {
                    method: 'GET',
                    credentials: 'include',
                })
                .then((resp) => {
                    return resp.json();
                })
                .then((data) => {
                    array.push({
                        id: (k - i),
                        content_id: props.ratings[i].content_id,
                        rating: props.ratings[i].rating,
                        name: data.name,
                        uri: data.uri
                    });
                    console.log(array);
                    // return data;
                })
                .catch((err) => {
                    return console.error(err);
                })
            }
        }
        console.log('Fetch over!');
        console.log(array);
        fetchedContentInfo = array.map((array) => {
            return(
                <li key={array.id}>
                    {array.name}
                </li>
            );
        });
        console.log(fetchedContentInfo);
    };


    return (
        <div>
            <h1>Ratings</h1>
            <div>
                <h2>Track ratings:</h2>
                {fetchedContentInfo}
            </div>
        </div>
    );
};

export default Ratings;
